var webApp = angular.module('socialNetworkApp', ["ngRoute", "ngCookies", "pascalprecht.translate"]);
webApp.config(function ($routeProvider, $translateProvider) {
    $routeProvider.when('/',
        {
            templateUrl: 'views/components/home/home.html',
            controller: 'homeController'
        });
    $routeProvider.when('/login',
        {
            templateUrl: 'views/components/login/login.html',
            controller: 'loginController'
        });

    $routeProvider.otherwise({redirectTo: '/'});

    $translateProvider.useCookieStorage();
    $translateProvider.useStaticFilesLoader({
        prefix: 'assets/i18n/',
        suffix: '.json'
    });
    $translateProvider.preferredLanguage('en');
}).run(function($rootScope, $location) {
    $rootScope.loggedInUser = false;

    $rootScope.$on("$routeChangeStart", function(event, next, current) {
        if ($rootScope.loggedInUser === false) {
            if (!((next.templateUrl == "views/components/registry/view.html")
                || (next.templateUrl == "views/components/login/login.html"))) {
                $location.path("/login");
            }
        }
        else {
            if (next.templateUrl == "views/components/login/login.html") {
                $location.path("/");
            }
        }
    });
});
webApp.controller("homeController", ['$scope', function($scope){

}]);
webApp.controller("loginController", ['$scope', '$location', 'loginService', function($scope, $location, loginService){
    $scope.loginUser = function () {
        var promiseObj = loginService.doLogin($scope.username, $scope.password);
        promiseObj.then(function(value) {
            $rootScope.userInfo = value;
            $rootScope.loggedInUser = true;
            $location.path('/');
        },
        function (value) {
            $scope.username = "";
            $scope.password = "";
        });
    };
}]);
webApp.factory('loginService',['$http', '$q', function ($http, $q) {
    return{
        doLogin: function (username, password) {
            var deferred = $q.defer();
            $http({
                method: 'POST',
                url: '/login',
                headers: {
                    'Content-Type': 'application/x-www-form-urlencoded; charset=utf-8'
                },
                data: 'username=' + username + '&password=' + password
            }).
            then(function(response) {
                    deferred.resolve(response.data);
                },
                function(response) {
                    deferred.reject(response.status);
                });

            return deferred.promise;
        }
    }
}]);


webApp.directive('langSelector', function($translate) {
    return {
        restrict: 'C',
        replace: true,
        templateUrl: 'views/shared/lang/lang-selector.html',
        link: function(scope, elem, attrs) {
            scope.setLocale = function(locale) {
                $translate.use(locale);
            };
        }
    };
});
webApp.directive('switchTemplate', function($rootScope) {
    return {
        restrict: 'C',
        replace: true,
        templateUrl: function (elem, attrs) {
            if ($rootScope.loggedInUser) {
                return 'views/shared/template/logged.html';
            } else {
                return 'views/shared/template/unlogged.html';
            }
        }
    };
});